define('text!views/shell.html',[],function () { return '<div>\r\n    <div class="navbar navbar-1337" role="navigation">\r\n        <div class="container">\r\n            <div class="navbar-header">\r\n                <button type="button" class="navbar-toggle" data-toggle="collapse" data-target=".navbar-collapse">\r\n                    <span class="sr-only">Toggle navigation</span>\r\n                    <span class="icon-bar"></span>\r\n                    <span class="icon-bar"></span>\r\n                    <span class="icon-bar"></span>\r\n                </button>\r\n                <a class="navbar-brand" data-bind="attr: { href: router.navigationModel()[0].hash }">\r\n                    tretton37\r\n                </a>\r\n            </div>\r\n            <div class="navbar-collapse collapse">\r\n                <ul class="nav navbar-nav" data-bind="foreach: router.navigationModel">\r\n                    <li data-bind="css: { active: isActive }">\r\n                        <a data-bind="attr: { href: hash }, html: title"></a>\r\n                    </li>\r\n                </ul>\r\n                <!--<div class="loader nav navbar-nav navbar-right" data-bind="css: { active: router.isNavigating }">\r\n                    <i class="fa fa-spinner fa-spin fa-2x active"></i>\r\n                </div>-->\r\n            </div>\r\n        </div>\r\n    </div>\r\n\r\n    <div class="page-host container" data-bind="router: { transition: \'entrance\', cacheViews: true }"></div>\r\n</div>';});


